export default class {
    constructor(time) {
        this.time = time;
        
        this.onStartFn = 
        this.onEndFn = 
        this.onIntervalFn = () => {};

        this.remainingTime = time;
    }

    onStart(fn) {
        this.onStartFn = fn;
        return this;
    }

    onInterval(fn) {
        this.onIntervalFn = fn;
        return this;
    }

    onEnd(fn) {
        this.onEndFn = fn;
        return this;
    }

    start() {
        this.onStartFn();

        this.currentTime = 0;

        this.interval = setInterval(() => {
            this.currentTime++;
            this.onIntervalFn(this.currentTime);
            this.remainingTime --;
        }, 1000)

        this.timeout = setTimeout(() => {
            this.onEndFn();
            this.stop();
        }, this.time * 1000);

        return this;
    }

    stop() {
        if (this.interval) {
            clearInterval(this.interval);
        }
        if (this.timeout) {
            clearTimeout(this.timeout);
        }
        console.log("remaining", this.remainingTime);
        return this;
    }

    resume() {
        this.time = this.remainingTime;
        return this;
    }
}
