import React from 'react';

import Pomodoro from "./components/pomodoro";

function App() {
  const timer = ({time, endFn, intervalFn, startFn}) => {
    let timeCounter = 0;

    startFn = startFn ? startFn() : null;

    const interval = setInterval(() => {
      console.log(timeCounter);
      timeCounter++;

      if (timeCounter === time) {

        clearInterval(interval);

        endFn = endFn ? endFn() : null; 
      } 
    }, 1000)
  }

  timer({
    time: 5,
    startFn: () => {
      console.log("start");
    },
    endFn: () => {
      console.log("end");
    }
  })


  return (
    <Pomodoro
      onChangeBreakLength={() => console.log(1)}
      onChangeSessionLength={() => console.log(1)}

      onClickStart={() => console.log(1)}
      onClickStop={() => console.log(1)}
      onClickReset={() => console.log(1)}

      display={"00:00:00"}
    />
  );
}

export default App;
