import {
    onUppdateTime
}
from "./actions";

import {
    secondsToTimeString,
    concatObjects
}
from "../utilities"

const initState = {
    currentTime: "00:00"
};

const reducer = (state = initState, action) => {
    switch(action.type) {
        case onUppdateTime.getName: 
            return concatObjects(state, {
                currentTime: secondsToTimeString(action.seconds)
            })
        
        default:
            return state;
    }
};

export default reducer;
