export default class {
    constructor(time) {
        this.time = time;
        
        this.onStartFn = 
        this.onEndFn = 
        this.onIntervalFn = () => {};

        this.remainingTime = time;
        this.currentTime = 0;
    }

    onStart(fn) {
        this.onStartFn = fn;
        return this;
    }

    onInterval(fn) {
        this.onIntervalFn = fn;
        return this;
    }

    onEnd(fn) {
        this.onEndFn = fn;
        return this;
    }

    start() {
        if (this.isStarted) {
            this.onStartFn();

            if (this.remainingTime === 0) {
                this.reset();
            }

            this.interval = setInterval(() => {
                this.currentTime++;
                this.onIntervalFn(this.remainingTime);
                this.remainingTime = this.remainingTime - 1;
            }, 1000)

            this.timeout = setTimeout(() => {
                this.onEndFn();
                this.stop();
            }, this.remainingTime * 1000);

        }
        this.isStarted = true;

        return this;
    }

    stop() {
        if (this.interval) {
            clearInterval(this.interval);
        }

        if (this.timeout) {
            clearTimeout(this.timeout);
        }
        
        return this;
    }

    reset() {
        this.remainingTime = this.time;
        this.currentTime = 0;

        return this;
    }
}
