import React from 'react';

import Pomodoro from "./components/pomodoro";

function App() {
  const timer = ({timeOut, startFn, endFn, intervalFn}) => {
    startFn = startFn ? startFn: null;
    intervalFn = intervalFn ? intervalFn: (function(){}());
    endFn = endFn ? endFn: null; 


    //Start fn
    startFn();

    let currentTime = 0;

    //Interval fn
    const interval = setInterval(() => {
      currentTime++;

     intervalFn(currentTime);
    }, 1000)

    //End fn
    setTimeout(() => {
      endFn(timeOut);

      clearInterval(interval);

    }, timeOut)
  }

  timer({
    timeOut: 5000,

    startFn: () => {
      console.log("start");
    },

    endFn: timeOut => {
      console.log("end", timeOut)
    },

    intervalFn: currentTime => {
      console.log(currentTime);
    }


  })


  return (
    <Pomodoro
      onChangeBreakLength={() => console.log(1)}
      onChangeSessionLength={() => console.log(1)}

      onClickStart={() => console.log(1)}
      onClickStop={() => console.log(1)}
      onClickReset={() => console.log(1)}

      display={"00:00:00"}
    />
  );
}

export default App;
