import {
    onUppdate 
}
from "./actions";

import {
    secondsToTimeString
}
from "../utilities"

const initState = "00:00";

const reducer = (state = initState, action) => {
    switch(action.type) {
        case onUppdate.getAction: 
            return secondsToTimeString(action.seconds);
        
        default:
            return state;
    }
};

export default reducer;
