export default class {
    constructor(time) {
        this.time = time;
        
        this.onStartFn = 
        this.onEndFn = 
        this.onIntervalFn = () => {};
    }

    onStart(fn) {
        this.onStartFn = fn;
        return this;
    }

    onInterval(fn) {
        this.onIntervalFn = fn;
        return this;
    }

    onEnd(fn) {
        this.onEndFn = fn;
        return this;
    }

    start() {
        this.onStartFn();
        this.timeout = setTimeout(() => {

        }, this.time);
    }
}
