import {
    onUppdateTime,
    onSetIndicator
}
from "./actions";

import {
    secondsToTimeString,
    concatObjects
}
from "../utilities"

const initState = {
    currentTime: "00:00",
    indicator: "session",
    isSession: true
};

const reducer = (state = initState, action) => {
    switch(action.type) {
        case onUppdateTime.getName: 
            return concatObjects(state, {
                currentTime: secondsToTimeString(action.seconds)
            });

        case onSetIndicator.getName:
            return concatObjects(state, {
                indicator: action.indicator,
                isSession: state.isSession === action.indicator
            });
        
        default:
            return state;
    }
};

export default reducer;
