import {
    onUppdateTime,
    onSetIndicator,
    onSetIsSession,
    onSetBreakTime,
    onSetSessionTime
}
from "./actions";

import {
    secondsToTimeString,
    concatObjects
}
from "../utilities"

const initState = {
    currentTime: "00:00",
    indicator: "session",
    isSession: true,
    sessionTime: 0,
    breakTime: 0
};

const reducer = (state = initState, action) => {
    switch(action.type) {
        case onUppdateTime.getName: 
            return concatObjects(state, {
                currentTime: secondsToTimeString(action.seconds)
            });

        case onSetIndicator.getName:
            return concatObjects(state, {
                indicator: action.indicator,
            });

        case onSetIsSession.getName:
            return concatObjects(state, {
                isSession: action.bool
            })

        case onSetBreakTime.getName: 
            return concatObjects(state, {
                breakTime: action.seconds
            });
        
        case onSetSessionTime.getName: 
            return concatObjects(state, {
                sessionTime: action.seconds
            })

        default:
            return state;
    }
};

export default reducer;
