export default seconds => {
    if (seconds === 0) return "00:00"
    const oneHourInSeconds = 60 * 60;
    const oneMinutInSeconds = 60;

    const [hours, minutesInHours] = (seconds/oneHourInSeconds).toString().split(".");
    
    const minutes = Math.round(parseFloat(`0.${minutesInHours}`) * oneMinutInSeconds);

    console.log(hours, minutes);    
};
