import React,{
  Component
} 
from 'react';

import Pomodoro from "./components/pomodoro";
import Timer from "./utilities/timer";
import timeStringToSeconds from "./utilities/timeStringToSeconds";

class App extends Component {
  constructor(props) {
    super(props)
    this.timer = new Timer();
  }

  onChangeBreakLengthHandler = event => {
    const seconds = timeStringToSeconds(event.target.value);
    this.timer.setTime(seconds);
  }

  render(){
    this.timer.onInterval(currentTime => {
      console.log(currentTime);
    });

    return (
      <Pomodoro
      onChangeBreakLength={event => this.onChangeBreakLengthHandler(event)}
      onChangeSessionLength={() => console.log(1)}

      onClickStart={() => this.timer.start()}
      onClickStop={() => this.timer}
      onClickReset={() => this.timer}
      onClickContinue={() => this.timer}

      display={"00:00:00"}/>
    );
  }
}

export default App;
