export default class {
    constructor(time) {
        this.time = time;
        
        this.onStartFn = 
        this.onEndFn = 
        this.onIntervalFn = () => {};
    }

    onStart(fn) {
        this.onStartFn = fn;
        return this;
    }

    onInterval(fn) {
        this.onIntervalFn = fn;
        return this;
    }

    onEnd(fn) {
        this.onEndFn = fn;
        return this;
    }

    start() {
        this.onStartFn();

        this.interval = setInterval(() => {
            this.onIntervalFn();
        }, 1000)

        this.timeout = setTimeout(() => {
            this.onEndFn();
            
            if (this.interval) {
                clearInterval(this.interval);
            }
        }, this.time);

        return this;
    }

    stop() {
        if (this.interval) {
            clearInterval(this.interval);
        }
    }
}
