import React from "react";
export default props => {
    const styles = {
        input: {
            textAlign: "center"
        },
        label: {
            display: "block"
        }
    };

    return (
        <div className="form-group">
            <label style={styles.label} for="formGroupExampleInput"> 
                {props.label} 
            </label>
            
            <input 
            style={styles.input}
            type={props.type} 
            className="form-control" 
            placeholder={props.placeholder}/>
        </div>
    )
};
