export default seconds => {
    if (seconds === 0) return "00:00"
    const oneHourInSeconds = 60 * 60;
    const oneMinutInSeconds = 60;

    let a = seconds/oneHourInSeconds;

    const [hours, minutesInHours] = a.toString().split(".");
    
    const minutes = Math.round(parseFloat(`0.${minutesInHours}`) * 60);

    console.log(hours, minutes);    
};
