import {setQoutes} from "./actions";

const initState = {
    qouteTexts: [],
    count: 0
}

const reducer = (state = initState, action) => {
    switch(action.type) {
        case setQoutes.getName:
            return {
                ...state,
                qouteTexts: 1
            }
        
        case "INCREMENT":
            return {
                ...state,
                count: state.count + action.value
            }

        default: 
            return state;
    }
};

export default reducer;
