import {setQoutes, selectRandomQoute} from "./actions";

import {
    concatArrays, 
    concatObjects,
    removeDubplicatesInArray,
    randomNumber
} 
from "../utilities"

const initState = {
    qoutes: [],
    qoutesAmount: 0,
    currentQoute: 0 
};

const reducer = (state = initState, action) => {
    switch(action.type) {
        case setQoutes.getName:
            return concatObjects(state, {
                qoutes: removeDubplicatesInArray(
                    concatArrays(
                        state.qoutes,
                        action.qoutes
                    )
                ),
                qoutesAmount: action.qoutes.length
            })
            
        case selectRandomQoute.getName:
            return concatObjects(state, {
                currentQoute: randomNumber(0, state.qoutesAmount - 1)
            })

        default: 
            return state;
    }
};

export default reducer;
