import {setQoutes} from "./actions";
import {concatArrays, removeDubplicatesInArray} from "../utilities"

const initState = {
    qouteTexts: []
}

const reducer = (state = initState, action) => {
    switch(action.type) {
        case setQoutes.getName:
            return {
                ...state,
                qouteTexts: removeDubplicatesInArray(
                    concatArrays(
                        state.qouteTexts,
                        action.qoutes
                    )
                )
            }

        default: 
            return state;
    }
};

export default reducer;
