import React, { Component, Fragment } from 'react';
import QouteGenerator from "./components/qoutePost/qoutePost";
import { connect } from "react-redux";
import { initQoutes, selectRandomQoute } from "./store/actions";

class App extends Component {
  componentWillMount() {
    this.props.onInitQoutes();
  }

  onClickFunctions() {
    this.props.onSelectRandomQoute()
  }

  render() {
    const {quote, author} = this.props.currentQoute;

    return (
      <Fragment>
        <QouteGenerator 
          text={quote} 
          source={author} 
          onClick={() => this.onClickFunctions()}/>
      </Fragment>
    );
  }
}
const mapStateToProps = state => ({
  qouteText: state.setQoutes.qouteText,
  currentQoute: state.selectRandomQoute.currentQoute
});

const mapDispatchToProps = dispatch => ({
  onInitQoutes: () => dispatch(initQoutes()),
  onSelectRandomQoute: () => dispatch(selectRandomQoute.getAction({
    qoutes: 1,
    qoutesAmount: 1
  }))
});

export default connect(mapStateToProps, mapDispatchToProps)(App);
