import React, { Component, Fragment } from 'react';
import QouteGenerator from "./components/qoutePost/qoutePost";
import { connect } from "react-redux";
import { initQoutes } from "./store/actions";

class App extends Component {
  componentWillMount() {
    this.props.onInitQoutes();
  }

  render() {
    const qoute = {
      text: `Life is 10% what happens to me and 90% of how I react to it.`,
      source: "Charles Swindoll"
    }

    return (
      <Fragment>
        <QouteGenerator 
          text={qoute.text} 
          source={qoute.source} 
          onClick={() => this.props.onInitQoutes()}/>
          <p className="text-dark">
            {this.props.qouteText}
          </p>
      </Fragment>
    );
  }
}
const mapStateToProps = state => ({
  qouteText: state.qouteText
});

const mapDispatchToProps = dispatch => ({
  onInitQoutes: () => dispatch(initQoutes())
});

export default connect(mapStateToProps, mapDispatchToProps)(App);
