import { createAction } from "../functions/createAction";
import { createAsyncAction } from "../functions/createAsyncAction";
import { randomQouetsUrl } from "../urls/randomQoutesUrl";

const setQoutes = createAction({
    name: "SET_QOUTES",
    payload: [
        "qoutes"
    ]
});

const initQoutes = createAsyncAction({
    setQoutes: setQoutes
}, 
({dispatch, actions}) => {
    fetch(randomQouetsUrl)
        .then(response => 
            response.json()
        )
        .then(response => {
            
            dispatch(
                actions.setQoutes({
                    qoutes: 1
                })
            )
        })
});

export {setQoutes, initQoutes};
