export const randomNumber = ({max, min}) => {
    let randomNum;

    if (!(max || min)) {
        randomNum = Math.random();
    }
    else {
        randomNum = Math.abs(
            Math.random() * (max - min) + min
        )
    }
    
    return randomNum;
}
