import { createAction } from "../functions/createAction";
import { createAsyncAction } from "../functions/createAsyncAction";
import { qouteUrl } from "../urls/urls";

const setQoutes = createAction({
    name: "SET_QOUTES",
    payload: [
        "qoutes"
    ]
});

const initQoutes = createAsyncAction({
    setQoutes: setQoutes
}, 
({dispatch, actions}) => {
    fetch(qouteUrl)
        .then(response => 
            response.json()
        )
        .then(response => {
            console.log(response);
            dispatch(actions.setQoutes)
        })
});

export {setQoutes, initQoutes};
